var nodemailer = require("nodemailer");

var smtpTransport = nodemailer.createTransport("SMTP",{
   service: "Gmail",  // sets automatically host, port and connection security settings
   auth: {
       user: "bloomboardapp@gmail.com",
       pass: "whitecollar"
   }
});

exports.WelcomeEmail = function (email) {
	smtpTransport.sendMail({  //email options
	   from: "Bloomboard <bloomboardapp@gmail.com>", // sender address.  Must be the same as authenticated user if using Gmail.
	   to: email, // receiver
	   subject: "Welcome To Bloomboard", // subject
	   text: "Welcome to Bloomboard!", // body
	   html: '<style type="text/css">body{ margin:0; padding:0; border:0; font-family: Geneva;}#title{font-size: 40;}#message{margin:0px; padding:130 0 0 60; border:0;font-size: 20;}#head{ width:100%; background:black; position:fixed;}#img{ margin:20 0 300 55; padding: 5px; }</style><body><div id="head"><img src="data:image/png;base64," alt="Welcome" /> </div><div id="message"><div id="title">Welcome to Bloomboard</div><br><br>Click the Button to Get Started!<br><br></div><div id="img"><a href="http://bloomboard-staging.herokuapp.com"><img src="data:image/png;base64," alt="See Your Boards" /></a></div></body>' // html body
	}, function(error, response){  //callback
	   if(error){
	       console.log(error);
	   }else{
	       console.log("Message sent: " + response.message);
	   }
	   
	   smtpTransport.close(); // shut down the connection pool, no more messages.  Comment this line out to continue sending emails.
	});
};

